module.exports = {
  reporter: function (res) {
    var res = res.map(x=>x.error);
    var errorArray = [];
    for (err of res) {
      var error = {};
      error.reason = err.reason;
      error.evidence = err.evidence;
      error.line = err.line;
      error.column = err.character;
      errorArray.push(error);
    }
    console.log(JSON.stringify(errorArray));
  }
  
};
